import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate, useParams } from 'react-router-dom';
import homeIcon from '../assets/home.png';
import dalleBot from '../assets/dalle_bot.jpg';
import sendButton from '../assets/send_button.png';
import '../styles/ChatPage.module.css';


const ChatPage = () => {
    const { category } = useParams();
    const location = useLocation();
    const navigate = useNavigate();
    const { prompt } = location.state || {};
    const [input, setInput] = useState('');
  useEffect(() => {
    const loadingElement = document.getElementById('loading');
    setTimeout(() => {
      if (loadingElement) loadingElement.style.display = 'none';
    }, 1500);
  }, []);
  const handleHomeClick = () => {
    navigate('/chatbot');
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the form submission to send a message
    console.log('User input:', input);
    setInput('');
  };

  return (
    <div>
      <div
        id="loading"
        style={{
          position: 'fixed',
          width: '100%',
          height: '100%',
          top: 0,
          left: 0,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          background: '#861F41',
          zIndex: 1000,
        }}
      >
        <img src={dalleBot} id="loading-image" alt="Loading" />
      </div>
      <div id="app">
        <div id="header">
          <button id="home-button" onClick={handleHomeClick}>
            <img src={homeIcon} alt="Home" />
          </button>
          <h1 id="chat_header">MindCraft VT Chatbot</h1>
          <select id="category-selector" defaultValue={category}>
            <option value="socialLife">Social Life</option>
            <option value="academia">Academia</option>
            <option value="wellness">Wellness</option>
            <option value="tutor">Tutor</option>
          </select>
        </div>
        <div id="chat_container">
          {/* Render chat messages here */}
        </div>
        <form onSubmit={handleSubmit}>
          <textarea
            name="prompt"
            required
            rows="1"
            cols="1"
            placeholder="Ask MindCraft VT..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
          ></textarea>
          <button type="submit" id="submit_button">
            <img src={sendButton} alt="Send" />
          </button>
        </form>
      </div>
    </div>
  );
};

export default ChatPage;
