import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate, useParams } from 'react-router-dom';
import homeIcon from '../assets/home.png';
import dalleBot from '../assets/dalle_bot.jpg';
import sendButton from '../assets/send_button.png';
import styles from '../styles/ChatPage.module.css';
import user from '../assets/vt_student.jpg';


const ChatPage = () => {
    const { category } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const { prompt } = location.state || {};
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [quest, setQuest] = useState('');

  useEffect(() => {
    const loadingElement = document.getElementById('loading');
    setTimeout(() => {
      if (loadingElement) loadingElement.style.display = 'none';
    }, 1500);

    if (prompt) {
      initialChatGPT(prompt);
    }
  }, [prompt]);

  const handleHomeClick = () => {
    showLoading();
    setTimeout(() => {
      navigate('/chatbot');
    }, 1500);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (input.trim() === "") {
      return;
    }

    const newMessage = { role: 'user', content: input };
    setMessages([...messages, newMessage]);
    setInput('');

    await handleChatGPT(input);
  };

  const handleChatGPT = async (message) => {
    const uniqueId = generateUniqueId();
    const newMessage = { role: 'assistant', content: 'Loading...', id: uniqueId };
    setMessages((prevMessages) => [...prevMessages, newMessage]);
    setIsLoading(true);

    const response = await sendMessageToChatGPT(message);

    setMessages((prevMessages) => {
      const updatedMessages = [...prevMessages];
      const index = updatedMessages.findIndex(msg => msg.id === uniqueId);
      if (index !== -1) {
        updatedMessages[index].content = response;
      }
      return updatedMessages;
    });

    setIsLoading(false);
    handleYesNoButtons(response, uniqueId);
  };

  const initialChatGPT = async (initialPrompt) => {
    setQuest(initialPrompt);
    setMessages([{ role: 'assistant', content: 'Loading...' }]);
    const response = await InitialMessageToChatGPT(initialPrompt);
    setMessages([{ role: 'assistant', content: response }]);
  };

  // API call to send a message to ChatGPT
  const sendMessageToChatGPT = async (prompt) => {
    const response = await fetch('https://api.openai.com/v1/chat/completions', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer sk-proj-Ujro97vuIFi0BvhkpHnET3BlbkFJ2eiNRzcGGHMHfman9iiE'
      },
      body: JSON.stringify({
        model: "gpt-3.5-turbo",
        messages: [{ role: "user", content: prompt }]
      })
    });
    const data = await response.json();
    if (data.choices && data.choices.length > 0) {
      const replyText = data.choices[0].message.content;
      return replyText;
    } else {
      return "Failed to get a response";
    }
  };

  // Initial API call to ChatGPT
  const InitialMessageToChatGPT = async (quest) => {
    const response = await fetch('https://api.openai.com/v1/chat/completions', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer sk-proj-ClyheMv50nSvIf1Q3g84T3BlbkFJoZ6isdGyOQEQcCm8GlzN'
      },
      body: JSON.stringify({
        model: "gpt-3.5-turbo",
        messages: [{ role: "system", content: quest }]
      })
    });
    const data = await response.json();
    if (data.choices && data.choices.length > 0) {
      const replyText = data.choices[0].message.content;
      return replyText;
    } else {
      return "Failed to get a response";
    }
  };

  const chatbotMapping = {
    'Social': 'socialLife',
    'Academia': 'academia',
    'Wellness': 'wellness',
    'tutor': 'tutor'
  };

  const getChatbotFromPrompt = (urlPrompt) => {
    for (const key in chatbotMapping) {
      if (urlPrompt.includes(key)) {
        return chatbotMapping[key];
      }
    }
    return null;
  };

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const currentChatbot = getChatbotFromPrompt(String(urlParams));
    const selector = document.getElementById('category-selector');

    if (selector) {
      if (currentChatbot) {
        selector.value = currentChatbot;
      } else {
        selector.selectedIndex = 0;
      }
    }

    const prompt_url = urlParams.get('prompt');
    if (prompt_url) {
      setQuest(prompt_url);
    }
  }, []);

  const generateUniqueId = () => {
    const timestamp = Date.now();
    const randomNumber = Math.random();
    const hexadecimalString = randomNumber.toString(16);

    return `id-${timestamp}-${hexadecimalString}`;
  };

  const chatStripe = (ai_bool, value, uniqueId) => {
    return `
      <div class="wrapper ${ai_bool ? styles.ai : ''}">
        <div class="chat">
          <div class="profile">
            <img
              src="${ai_bool ? dalleBot : user}"
              alt="${ai_bool ? "bot" : "user"}"
            />
          </div>
          <div class="message" id=${uniqueId}>${value}</div>
        </div>
      </div>
    `;
  };

  const loader = (element) => {
    element.textContent = "";
    let loadInterval = setInterval(() => {
      element.textContent += ".";
      if (element.textContent === "....") {
        element.textContent = "";
      }
    }, 300);
  };

  const typeText = (element, text, callback) => {
    let index = 0;
    let interval = setInterval(() => {
      if (index < text.length) {
        element.innerHTML += text.charAt(index);
        document.querySelector(`.${styles.chatContainer}`).scrollTop = document.querySelector(`.${styles.chatContainer}`).scrollHeight;
        ++index;
      } else {
        clearInterval(interval);
        if (callback) callback();
      }
    }, 20);
  };

  const generateEmailPrompt = (conversationHistory) => {
    return `
      You are a helpful assistant. Based on the following conversation history, generate a detailed and polite email for a Virginia Tech advisor. The email should be written from the perspective of a Virginia Tech student asking for more information or assistance based on their enquiry.

      Conversation History: ${conversationHistory.map(message => {
        return `\n${message.role === 'user' ? 'User' : 'Assistant'}: ${message.content}`;
      }).join('')}

      The name of the student is Roshan Ravindran.

      Please ensure the email includes:
      - A formal greeting.
      - A clear and detailed description of the student's enquiry.
      - Relevant details or context that the student has provided.
      - A polite closing statement thanking the advisor for their time and asking for more detailed information or assistance.
      - Keep it short and professional

      The email should be structured formally and professionally, appropriate for a university setting.
    `;
  };

  const handleYesNoButtons = (replyText, uniqueId) => {
    if (replyText.toLowerCase().includes("The")) {
      const yesButton = document.createElement("button");
      yesButton.className = "yes-button";
      yesButton.textContent = "Yes";
      const noButton = document.createElement("button");
      noButton.className = "no-button";
      noButton.textContent = "No";

      const buttonContainer = document.createElement("div");
      buttonContainer.id = "button-container";
      buttonContainer.appendChild(yesButton);
      buttonContainer.appendChild(noButton);
      document.querySelector(`.${styles.chatContainer}`).appendChild(buttonContainer);

      document.querySelector(`.${styles.chatContainer}`).scrollTop = document.querySelector(`.${styles.chatContainer}`).scrollHeight;

      noButton.addEventListener("click", () => {
        const noMessageDiv = document.createElement("div");
        noMessageDiv.className = "wrapper user";
        noMessageDiv.innerHTML = chatStripe(false, "No", uniqueId);
        document.querySelector(`.${styles.chatContainer}`).appendChild(noMessageDiv);
        const thankYouDiv = document.createElement("div");
        thankYouDiv.className = "wrapper ai";
        thankYouDiv.innerHTML = chatStripe(true, "Thank you! Please let me know if you have more questions.", uniqueId);
        document.querySelector(`.${styles.chatContainer}`).appendChild(thankYouDiv);
        document.querySelector(`.${styles.chatContainer}`).scrollTop = document.querySelector(`.${styles.chatContainer}`).scrollHeight;
        document.getElementById("button-container").remove();
      });

      yesButton.addEventListener("click", async () => {
        const yesMessageDiv = document.createElement("div");
        yesMessageDiv.className = "wrapper user";
        yesMessageDiv.innerHTML = chatStripe(false, "Yes", uniqueId);
        document.querySelector(`.${styles.chatContainer}`).appendChild(yesMessageDiv);
        const responseDiv = document.createElement("div");
        responseDiv.id = "bot-message";
        responseDiv.innerHTML = chatStripe(true, "Would you like to email the advisor or continue the conversation?", uniqueId);
        document.querySelector(`.${styles.chatContainer}`).appendChild(responseDiv);

        const emailButton = document.createElement("button");
        emailButton.className = "response-button";
        emailButton.textContent = "Email Advisor";
        const askMindcraftButton = document.createElement("button");
        askMindcraftButton.className = "response-button";
        askMindcraftButton.textContent = "Continue Chat";
        document.getElementById("button-container").remove();

        responseDiv.appendChild(document.createElement("br"));
        responseDiv.appendChild(emailButton);
        responseDiv.appendChild(askMindcraftButton);

        document.querySelector(`.${styles.chatContainer}`).scrollTop = document.querySelector(`.${styles.chatContainer}`).scrollHeight;

        emailButton.addEventListener("click", async () => {
          const emailMessageDiv = document.createElement("div");
          emailMessageDiv.className = "wrapper user";
          emailMessageDiv.innerHTML = chatStripe(false, "Yes, Please help me email my advisor", uniqueId);
          document.querySelector(`.${styles.chatContainer}`).appendChild(emailMessageDiv);

          const emailPrompt = generateEmailPrompt(messages);
          const emailResponse = await sendMessageToChatGPT(emailPrompt);
          const subjectMatch = emailResponse.match(/Subject: (.*)\n/);
          const subject = subjectMatch ? subjectMatch[1] : "Inquiry";
          const body = emailResponse.replace(/Subject: .*?\n/, "");

          const mailtoLink = `mailto:?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`;
          window.location.href = mailtoLink;

          document.getElementById("bot-message").remove();
          const thankYouDiv = document.createElement("div");
          thankYouDiv.className = "wrapper ai";
          thankYouDiv.innerHTML = chatStripe(true, "Hope I answered your question. Please let me know if you have more questions", uniqueId);
          document.querySelector(`.${styles.chatContainer}`).appendChild(thankYouDiv);
        });

        askMindcraftButton.addEventListener("click", () => {
          const askMindcraftMessageDiv = document.createElement("div");
          askMindcraftMessageDiv.className = "wrapper user";
          askMindcraftMessageDiv.innerHTML = chatStripe(false, "Continue chat", uniqueId);
          document.querySelector(`.${styles.chatContainer}`).appendChild(askMindcraftMessageDiv);
          const continueDiv = document.createElement("div");
          continueDiv.className = "wrapper ai";
          continueDiv.innerHTML = chatStripe(true, "Please provide more details or ask another question.", uniqueId);
          document.querySelector(`.${styles.chatContainer}`).appendChild(continueDiv);
          document.querySelector(`.${styles.chatContainer}`).scrollTop = document.querySelector(`.${styles.chatContainer}`).scrollHeight;
          document.getElementById("bot-message").remove();
        });
      });
    }
  };

  const showLoading = () => {
    document.getElementById('loading').style.display = 'flex';
  };

  return (
    <div className={styles.chatPage}> {/* Apply the scoped class */}
    <div>
      <div
        id="loading"
        style={{
          position: 'fixed',
          width: '100%',
          height: '100%',
          top: 0,
          left: 0,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          background: '#861F41',
          zIndex: 1000,
        }}
      >
        <img src={dalleBot} id={styles.loadingImage} alt="loading" />
      </div>
      <div id={styles.app}>
        <div id={styles.header}>
          <button id={styles.homeButton} onClick={handleHomeClick}>
            <img src={homeIcon} alt="Home" />
          </button>
          <h1 id={styles.chatHeader}>MindCraft VT Chatbot</h1>
          <select id={styles.categorySelector} defaultValue={category}>
            <option value="socialLife">Social Life</option>
            <option value="academia">Academia</option>
            <option value="wellness">Wellness</option>
            <option value="tutor">Tutor</option>
          </select>
        </div>
        <div id={styles.chatContainer} className={styles.chatContainer}>
            {messages.map((msg, index) => (
              <div key={index} className={`${styles.wrapper} ${msg.role === 'assistant' ? styles.ai : ''}`}>
                <div className={styles.chat}>
                  <div className={styles.profile}>
                    <img src={msg.role === 'assistant' ? dalleBot : user} alt={msg.role} />
                  </div>
                  <div className={styles.message} id={msg.id}>{msg.content}</div>
                </div>
              </div>
            ))}
          </div>
        <form onSubmit={handleSubmit}>
          <textarea
            name="prompt"
            required
            rows="1"
            cols="1"
            placeholder="Ask MindCraft VT..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
          ></textarea>
          <button type="submit" id={styles.submitButton}>
            <img src={sendButton} alt="Send" />
          </button>
        </form>
      </div>
    </div>
    </div>
  );
};

export default ChatPage;
