  const handleGeneratePicture = async () => {
    setLoading(true);
    try {
      const fullPrompt = `Generate a profile picture that is suitable for a personal online profile. The picture should be visually appealing, and related to Virginia Tech. The profile picture is for a chatbot profile. ${prompt}`;
      const response = await fetch('https://api.openai.com/v1/images/generations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer sk-proj-ClyheMv50nSvIf1Q3g84T3BlbkFJoZ6isdGyOQEQcCm8GlzN'
        },
        body: JSON.stringify({
          prompt: fullPrompt,
          n: 1,
          size: "1024x1024"
        })
      });
      const data = await response.json();
      if (data.data && data.data.length > 0) {
        const imageUrl = data.data[0].url;
        console.log("Image URL:", imageUrl);
        setFile(imageUrl);
        toast.success("Image generated successfully!");
      } else {
        toast.error("Failed to get a response");
      }
